import React, {Component} from 'react';
import Header from './components/Header'
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = { monsters: [
      {name: 'Monstert 1'},
      {name: 'Monstert 2'},
      {name: 'Monstert 3'},
      {name: 'Monstert 4'}
    ] 
   }
  }

  componentDidMount(){
    fetch('https://jsonplaceholder.typicode.com/users')
    .then((respons) => {
       respons.json()
       .then((user) => {
              this.setState({monsters: user})
       })
    })
  }

  render() { 
    return (  
      <div className='App'>
        <Header/>
        {this.state.monsters.map((monster) => {
          return (
            <div key={monster.name}>
              <h3>{monster.name}</h3>
            </div>
          )
        })}
      </div>
    );
  }
}


export default App;
